34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;    cout&lt;span class=&#34;token operator&#34;&gt;&amp;lt;&amp;lt;&lt;/span&gt;g&lt;span class=&#34;token punctuation&#34;&gt;[&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;1&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;]&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;[&lt;/span&gt;n&lt;span class=&#34;token punctuation&#34;&gt;]&lt;/span&gt;&lt;span class=&#34;token operator&#34;&gt;&amp;lt;&amp;lt;&lt;/span&gt;&lt;span class=&#34;token string&#34;&gt;&#34;\n&#34;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;40&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;  &lt;span class=&#34;token punctuation&#34;&gt;&amp;#125;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;41&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;  &lt;span class=&#34;token keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;0&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;42&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token punctuation&#34;&gt;&amp;#125;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

 &lt;h1 id=&#34;拼图游戏-第四课广搜-中级班517编程普及组&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#拼图游戏-第四课广搜-中级班517编程普及组&#34;&gt;#&lt;/a&gt; 拼图游戏 - 第四课：广搜 -【中级班】517 编程普及组&lt;/h1&gt;
&lt;p&gt;&lt;img data-src=&#34;https://i.loli.net/2021/07/25/4xMjbA9NZi2sHVD.png&#34; alt=&#34;拼图游戏-第四课：广搜-【中级班】517编程普及组&#34;&gt;&lt;/p&gt;
&lt;span id=&#34;more&#34;&gt;&lt;/span&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
using namespace std;

int mod=1000007;
int ji[1000010];
int top;
int me[1000010],to[1000010];

int X[4]=&amp;#123;0,1&amp;#125;;
int Y[4]=&amp;#123;1,0&amp;#125;;

struct zb &amp;#123;
  int a[5][5];
  int ans;
&amp;#125;;
queue&amp;lt;zb&amp;gt;q;

void out(int a[5][5]) &amp;#123;
  for(int i=1;i&amp;lt;=3;i++) &amp;#123;
    for(int j=1;j&amp;lt;=3;j++) &amp;#123;
      cout&amp;lt;&amp;lt;a[i][j];
    &amp;#125;
    cout&amp;lt;&amp;lt;endl;
  &amp;#125;
&amp;#125;

void outji() &amp;#123;
  cout&amp;lt;&amp;lt;top&amp;lt;&amp;lt;endl;
  for(int i=0;i&amp;lt;mod;i++) &amp;#123;
    if(ji[i]) &amp;#123;
      for(int j=ji[i];;j=to[j]) &amp;#123;
        cout&amp;lt;&amp;lt;me[j]&amp;lt;&amp;lt;&amp;quot;--&amp;gt;&amp;quot;;
        if(to[j]==0) &amp;#123;
          break;
        &amp;#125;
      &amp;#125;
      cout&amp;lt;&amp;lt;ji[i]&amp;lt;&amp;lt;endl;
    &amp;#125;
  &amp;#125;
&amp;#125;

int copy(int &amp;amp;a,int b[5][5]) &amp;#123;
  a=0;
  for(int i=1;i&amp;lt;=3;i++) &amp;#123;
    for(int j=1;j&amp;lt;=3;j++) &amp;#123;
      a=a*10+b[i][j];
    &amp;#125;
  &amp;#125;
  return a;
&amp;#125;

bool find(int x) &amp;#123;
  for(int i=ji[x%mod];to[i]!=0;i=to[i]) &amp;#123;
    if(me[i]==x) &amp;#123;
      return true;
    &amp;#125;
  &amp;#125;
  return false;
&amp;#125;

void in(int x) &amp;#123;
  if(ji[x%mod]==0) &amp;#123;
    top++;
    ji[x%mod]=top;
    me[top]=x;
    return;
  &amp;#125;
  for(int i=ji[x%mod];;i=to[i]) &amp;#123;
    if(to[i]==0) &amp;#123;
      top++;
      to[i]=top;
      me[top]=x;
      return;
    &amp;#125;
  &amp;#125;
&amp;#125;

zb push(int a[5][5],int ans) &amp;#123;
  zb x;
  for(int i=1;i&amp;lt;=3;i++) &amp;#123;
    for(int j=1;j&amp;lt;=3;j++) &amp;#123;
      x.a[i][j]=a[i][j];
    &amp;#125;
  &amp;#125;
  x.ans=ans;
  q.push(x);
  return x;
&amp;#125;

int bfs(int a[5][5]) &amp;#123;

  zb k;
  int h;

  push(a,0);
  in(copy(h,a));

  while(!q.empty()) &amp;#123;

    k=q.front();
    q.pop();

    copy(h,k.a);
    if(h==12345678) &amp;#123;
      return k.ans;
    &amp;#125;

    //cout&amp;lt;&amp;lt;k.ans&amp;lt;&amp;lt;endl;
    //out(k.a);

    for(int x=1;x&amp;lt;=3;x++) &amp;#123;
      for(int y=1;y&amp;lt;=3;y++) &amp;#123;
        for(int i=0;i&amp;lt;2;i++) &amp;#123;
          if(x+X[i]&amp;gt;3||y+Y[i]&amp;gt;3);
          else &amp;#123;
            swap(k.a[x][y],k.a[x+X[i]][y+Y[i]]);
            copy(h,k.a);
            if(!find(h)) &amp;#123;
              in(h);
              push(k.a,k.ans+1);
            &amp;#125;
            swap(k.a[x][y],k.a[x+X[i]][y+Y[i]]);
          &amp;#125;
        &amp;#125;
        /*
        if(find()) &amp;#123;
          return;
        &amp;#125;
        for(int i=0;i&amp;lt;4;i++) &amp;#123;
          if(x+X[i]&amp;lt;=0||y+Y[i]&amp;lt;=0||x+X[i]&amp;gt;3||y+Y[i]&amp;gt;3||vis[x+X[i]][y+Y[i]]);
          else &amp;#123;
            vis[x][y]=true;
            dfs(x+X[i],y+Y[i]);
            swap(a[x][y],a[x+X[i]][y+Y[i]]);
            //cout&amp;lt;&amp;lt;&amp;quot;swap &amp;quot;&amp;lt;&amp;lt;a[x][y]&amp;lt;&amp;lt;&amp;quot; and &amp;quot;&amp;lt;&amp;lt;a[x+X[i]][y+Y[i]]&amp;lt;&amp;lt;endl;
            dfs(x+X[i],y+Y[i],ansx+1);
            swap(a[x][y],a[x+X[i]][y+Y[i]]);
            vis[x][y]=false;
          &amp;#125;
        &amp;#125;
        if(l==k) &amp;#123;
          l=q.back();
          ans++;
        &amp;#125;
        */
      &amp;#125;
    &amp;#125;
    //outji();
  &amp;#125;
  return 0;
&amp;#125;

int main() &amp;#123;

  int x;
  int a[5][5];

  for(int i=1;i&amp;lt;=3;i++) &amp;#123;
    cin&amp;gt;&amp;gt;x;
    for(int j=3;j&amp;gt;=1;j--) &amp;#123;
      a[i][j]=x%10;
      x/=10;
    &amp;#125;
  &amp;#125;

  int ans;
  copy(ans,a);
  if(ans==12345678) &amp;#123;
    cout&amp;lt;&amp;lt;0&amp;lt;&amp;lt;endl;
  &amp;#125; else &amp;#123;
    cout&amp;lt;&amp;lt;bfs(a)&amp;lt;&amp;lt;endl;
  &amp;#125;

  return 0;
&amp;#125;
